mapper.select:
SELECT COUNT(*) FROM "BOARD"
WHERE BOARD_DEL_FL = 'N'
AND BOARD_CODE = #{boardCode}

SELECT * FROM "BOARD_IMG"
WHERE BOARD_NO = #{boardNo}
ORDER BY IMG_ORDER

mapper.update:
UPDATE "BOARD" SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM "BOARD_LIKE"
WHERE MEMBER_NO = #{memberNo}
AND BOARD_NO = #{boardNo}
